import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";

const About = () => {
  const aboutRef = useRef(null);
  const titleRef = useRef(null);
  const textRef = useRef(null);
  const descRef = useRef(null);

  useEffect(() => {
    const tl = gsap.timeline({ defaults: { duration: 1, ease: "power3.out" } });

    tl.from(titleRef.current, { opacity: 0, y: -50, delay: 0.3 }) // Animation du titre
      .from(textRef.current, { opacity: 0, x: -50 }, "-=0.5") // Animation du texte gauche
      .from(descRef.current, { opacity: 0, x: 50 }, "-=0.5"); // Animation du texte droit
  }, []);

  return (
    <div
      ref={aboutRef}
      name="about"
      className="bg-[#0a192f] w-full h-screen text-gray-300 flex justify-center items-center"
    >
      <div className="max-w-[1000px] w-full flex flex-col justify-center items-center">
        {/* Titre */}
        <div className="grid grid-cols-2 gap-8 w-full">
          <div className="sm:text-right pb-8 pl-4">
            <p ref={titleRef} className="text-4xl font-bold inline border-b-4 border-pink-600">
              About
            </p>
          </div>
        </div>

        {/* Contenu */}
        <div className="max-w-[1000px] w-full grid sm:grid-cols-2 gap-8 px-4">
          <div ref={textRef} className="sm:text-right text-4xl font-bold">
            <p>Hi, I'm Mohamed Ramy. Nice to meet you! Please take a look around.</p>
          </div>
          <div ref={descRef}>
            <p>
              Lorem ipsum dolor sit amet consectetur, adipisicing elit. Labore voluptates quam illo facere illum
              voluptatum quae itaque? Eos laborum mollitia obcaecati enim, quasi dolores facere odio quidem, beatae est
              commodi.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default About;
