import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const About = () => {
  const aboutRef = useRef(null);
  const titleRef = useRef(null);
  const leftTextRef = useRef(null);
  const rightTextRef = useRef(null);

  useEffect(() => {
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: aboutRef.current,
        start: "top 80%", // L'animation démarre quand 80% de la section est visible
        toggleActions: "play none none none", // Joue l'animation une seule fois
      },
    });

    // Animation du titre
    tl.from(titleRef.current, { opacity: 0, y: -50, duration: 1 });

    // Animation du texte de gauche (viens de la gauche)
    tl.from(leftTextRef.current, { opacity: 0, x: -100, duration: 1 }, "-=0.5");

    // Animation du texte de droite (viens du haut)
    tl.from(rightTextRef.current, { opacity: 0, y: -100, duration: 1 }, "-=0.5");
  }, []);

  return (
    <div
      ref={aboutRef}
      name="about"
      className="bg-[#0a192f] w-full h-screen text-white flex justify-center items-center"
    >
      <div className="max-w-[1000px] w-full flex flex-col justify-center items-center">
        {/* Titre */}
        <div className="grid grid-cols-2 gap-8 w-full">
          <div className="sm:text-right pb-8 pl-4">
            <p ref={titleRef} className="text-4xl font-bold inline border-b-4 border-pink-600">
              About
            </p>
          </div>
        </div>

        {/* Contenu */}
        <div className="max-w-[1000px] w-full grid sm:grid-cols-2 gap-8 px-4">
          <div ref={leftTextRef} className="sm:text-right text-4xl font-bold">
            <p>Hi, I'm Nice to meet you! Please take a look around.</p>
          </div>
          <div ref={rightTextRef}>
            <p  className="text-white-500 font-bold inline border-b-4">
              Lorem ipsum dolor sit amet consectetur, adipisicing elit. Labore voluptates quam illo facere illum
              voluptatum quae itaque? Eos laborum mollitia obcaecati enim, quasi dolores facere odio quidem, beatae est
              commodi.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default About;
