import React, { useEffect, useRef } from 'react';
import workimg from '../assets/workImg.jpeg';
import realEstate from '../assets/realestate.jpg';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const projects = [
  { img: workimg, title: 'React JS' },
  { img: realEstate, title: 'Real Estate' },
  { img: workimg, title: 'React JS' },
  { img: realEstate, title: 'Real Estate' },
  { img: workimg, title: 'React JS' },
  { img: realEstate, title: 'Real Estate' }
];

const Work = () => {
  const workRef = useRef(null);

  useEffect(() => {
    const items = workRef.current.querySelectorAll('.content-div');

    gsap.from(items, {
      opacity: 0,
      scale: 0.8,
      y: 50,
      duration: 1,
      stagger: 0.5,
      ease: 'power2.out',
      scrollTrigger: {
        trigger: workRef.current,
        start: 'top 80%', 
      },
    });
  }, []);

  return (
    <div name="work" className="w-full md:h-full bg-[#0a192f] text-gray-300">
      <div ref={workRef} className="max-w-[1000px] mx-auto p-4 flex flex-col justify-center w-full h-full">
        <div className="pb-8">
          <p className="text-4xl font-bold inline border-b-4 text-gray-300 border-pink-600">
            Work
          </p>
          <p className="py-4">Check out some of my recent work</p>
        </div>

        <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-4">
          {projects.map((project, index) => (
            <div
              key={index}
              style={{
                backgroundImage: `url(${project.img})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
              }}
              className="shadow-lg shadow-[#040c16] group container rounded-md flex justify-center items-center mx-auto content-div transition-transform transform hover:scale-105"
            >
              {/* Hover effects */}
              <div className="opacity-0 group-hover:opacity-80 transition-opacity duration-300">
                <span className="text-2xl font-bold text-white tracking-wider">{project.title}</span>
                <div className="pt-8 text-center">
                  <a href="/">
                    <button className="text-center rounded-lg px-4 py-3 m-2 bg-white text-gray-700 font-bold text-lg">
                      Demo
                    </button>
                  </a>
                  <a href="/">
                    <button className="text-center rounded-lg px-4 py-3 m-2 bg-white text-gray-700 font-bold text-lg">
                      Code
                    </button>
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Work;
